// 身份证号码验证   
jQuery.validator.addMethod("isCardId", function(value, element) {
	//var cardId = /^\d{6}(18|19|20)?\d{2}(0[1-9]|1[12])(0[1-9]|[12]\d|3[01])\d{3}(\d|X)$/i;
	var cardId = /(^[1-9]\d{5}(18|19|([23]\d))\d{2}((0[1-9])|(10|11|12))(([0-2][1-9])|10|20|30|31)\d{3}[0-9Xx]$)|(^[1-9]\d{5}\d{2}((0[1-9])|(10|11|12))(([0-2][1-9])|10|20|30|31)\d{2}$)/;
	return this.optional(element) || (cardId.test(value));
}, "请输入正确的身份证号码");

//手机号码验证
jQuery.validator.addMethod("isPhone", function(value, element) {
	var phone=/^[1][3,4,5,7,8][0-9]{9}$/;
	return this.optional(element) || (phone.test(value));
}, "请输入正确的手机号码");

